import React, { useState, useEffect } from 'react'
import { Link } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import {collection, getDocs, getDoc, deleteDoc, doc, addDoc} from 'firebase/firestore'
import { db } from '../firebaseConfig/firebase'
import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import { async } from '@firebase/util'
const MySwal = withReactContent(Swal)

const Show = () => {

  const [ nombrereal, setnombrereal ] = useState('')

  const productsCollection = collection(db, "usuarios")

  const store = async (e) => {

    e.preventDefault()

    await addDoc( productsCollection, { nombrereal: nombrereal} )
    window.location.href = window.location.href;
    // or
    window.location.replace('');
  }


  const [products, setProducts] = useState( [] )

  const getProducts = async ()   => {
   const data = await getDocs(productsCollection)
   setProducts(
       data.docs.map( (doc) => ( {...doc.data(),id:doc.id}))

   )
  }
  //4 - Funcion para eliminar un doc
  const deleteProduct = async (id) => {
   const productDoc = doc(db, "usuarios", id)
   await deleteDoc(productDoc)
   getProducts()
  }
  const confirmDelete = (id) => {
    MySwal.fire({
      title: 'Â¿Elimina el producto?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      if (result.isConfirmed) { 
        //llamamos a la fcion para eliminar   
        deleteProduct(id)               
        Swal.fire(
          'Deleted!',
          'Your file has been deleted.',
          'success'
        )
      }
    })    
  }
  //6 - usamos useEffect
  useEffect( () => {
    getProducts()
    // eslint-disable-next-line
  }, [] )
  //7 - devolvemos vista de nuestro componente
  return (
    <div class="container">
  <div class="row">

    <div class="col">
    <h1>Alta Villanos</h1>

                 <form onSubmit={store}>
                    <div className='mb-3'>
                        <label className='form-label'>Nombre</label>
                        <input
                            value={nombrereal}
                            onChange={ (e) => setnombrereal(e.target.value)} 
                            type="text"
                            className='form-control'
                            required
                        />
                    </div>  
                    <button type='submit' className='btn btn-primary'>Enviar</button>
                 </form>   
    </div>
    <div class="col">
    <table className='table table-hover'>
            <thead>
              <tr>
                <th>Nombre real</th>
              </tr>
            </thead>
            <tbody>
              { products.map( (product) => (
                <tr key={product.id}>
                  <td>{product.name}</td>
                  <td>
                    <Link to={`/edit/${product.id}`} className="btn btn-light"><i className="fa-solid fa-pencil"></i></Link>
                    <button onClick={ () => { confirmDelete(product.id) } } className="btn btn-danger"><i className="fa-solid fa-trash"></i></button>
                  </td>
                </tr>                
              )) }
            </tbody>
          </table>
    </div>
  </div>
  </div>
  )
}

export default Show
