import withReactContent from 'sweetalert2-react-content'



const MySwal = withReactContent(Swal)

const Show = () => {
  const [nombrereal, setnombrereal] = useState('')
  const [nombredevillano, setnombredevillano] = useState('')
  const [edad, setedad] = useState('')
  const [genero, setGenero] = useState('No se envio nada')
  const [origen, setOrigen] = useState('No se envio nada')
  const [poder, setpoder] = useState(null)
  const [SuperHabilidades, setSuperHabilidades] = useState({
    habilidades: [],
    response: [],
})

  const productsCollection = collection(db, "heruesyvillanos")
  const store = async (e) => {
    e.preventDefault()
    await addDoc(productsCollection, { nombrereal: nombrereal, nombredevillano: nombredevillano, edad: edad, genero: genero, origen: origen, poder:SuperHabilidades.habilidades })
    window.location.href = window.location.href;
    window.location.replace('');
  }

  const uncheckAll = () => {
    document.querySelectorAll('#formElement input[type=checkbox]').forEach(function (checkElement) {
        checkElement.checked = false;
    })
    document.querySelectorAll('#formElement input[type=radio]').forEach(function (checkElement) {
        checkElement.checked = false;
    })
}

  const [products, setProducts] = useState([])
  const getProducts = async () => {
    const data = await getDocs(productsCollection)

    setProducts(
      data.docs.map((doc) => ({ ...doc.data(), id: doc.id }))

    )

  }

  const deleteProduct = async (id) => {
    const productDoc = doc(db, "heruesyvillanos", id)
    await deleteDoc(productDoc)
    getProducts()
  }
  const confirmDelete = (id) => {
    MySwal.fire({
      title: '¿Elimina el producto?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#d33',
      cancelButtonColor: '#3085d6',
      confirmButtonText: 'Yes, delete it!'
    }).then((result) => {
      if (result.isConfirmed) {
        deleteProduct(id)
        Swal.fire(
          'Deleted!',
          'Your file has been deleted.',
          'success'
        )
      }
    })
  }

  useEffect(() => {
    getProducts()

  }, [])

  const handleChange = (e) => {
    const { value, checked } = e.target
    const { habilidades } = SuperHabilidades;

    if (checked) {
        setSuperHabilidades({
            habilidades: [...habilidades, value],
            response: [...habilidades, value]
        })
    }
    else {
        setSuperHabilidades({
            habilidades: habilidades.filter((e) => e !== value),
            response: habilidades.filter((e) => e !== value),
        })
    }
}


  return (
    <div class="container">
      <div class="row">

        <div class="col">
          <h1>Creación de Super Héroes</h1>

          <form onSubmit={store}>
            <div className='mb-3'>
              <label className='form-label'>Nombre real</label>
              <input
                value={nombrereal}
                onChange={(e) => setnombrereal(e.target.value)}
                type="text"
                className='form-control'
                required
              />
            </div>
            <div className='mb-3'>
              <label className='form-label'>Nombre SuperHeroe</label>
              <input
                value={nombredevillano}
                onChange={(e) => setnombredevillano(e.target.value)}
                type="text"
                className='form-control'
                required
              />
            </div>
            <div className='mb-3'>
              <label className='form-label'>Edad</label>
              <input
                value={edad}
                onChange={(e) => setedad(e.target.value)}
                type="text"
                className='form-control'
                required
              />
            </div>
            <div className='mb-3'>
              <label className='form-label'>Genero</label>
              <br />
              <label className='form-label'>Hombre</label>
              <input type="radio" name='sexo' value='Hombre' id='campo1' onChange={(e) => setGenero(e.target.value)} />
              <br />
              <label className='form-label'>Mujer</label>
              <input type="radio" name='sexo' value='Mujer' id='campo2' onChange={(e) => setGenero(e.target.value)} />
              <br />
              <label className='form-label'>No binario</label>
              <input type="radio" name='sexo' value='No binario' onChange={(e) => setGenero(e.target.value)} />
            </div>
            <div className='mb-3'>
              <label className='form-label'><b>Origen</b></label>
              <br />
              <label className='form-label'>Natural Humano</label>
              <input type="radio" name='origen' value=' Natural Humano,' onChange={(e) => setOrigen(e.target.value)} required />
              <br />
              <label className='form-label'>Extraterrestre</label>
              <input type="radio" name='origen' value='Extraterrestre' onChange={(e) => setOrigen(e.target.value)} required />
              <br />
              <label className='form-label'>Experimento Científico</label>
              <input type="radio" name='origen' value='Experimento Científico' onChange={(e) => setOrigen(e.target.value)} required />
              <br />
              <label className='form-label'>Mutante</label>
              <input type="radio" name='origen' value='Mutante' onChange={(e) => setOrigen(e.target.value)} required />
            </div>





            <div className='mb-3'>
            <label>Características</label>
                Volador   <input type='checkbox' name="habilidades" value='Volador' onChange={handleChange} />&nbsp;
                Velocidad   <input type='checkbox' name="habilidades" value='Velocidad' onChange={handleChange} />&nbsp;
                Fuerza   <input type='checkbox' name="habilidades" value='Fuerza' onChange={handleChange} />&nbsp;
                Mutante   <input type='checkbox' name="habilidades" value='Mutante' onChange={handleChange} />&nbsp;
                Visión   <input type='checkbox' name="habilidades" value='Visión' onChange={handleChange} />&nbsp;
                Oído   <input type='checkbox' name="habilidades" value='Oído' onChange={handleChange} />&nbsp;
                Invulneravilidad   <input type='checkbox' name="habilidades" value='Invulneravilidad' onChange={handleChange} />&nbsp;
                Telepatia   <input type='checkbox' name="habilidades" value='Telepatia' onChange={handleChange} />&nbsp;
                Telequinesis   <input type='checkbox' name="habilidades" value='Lanza Rayos' onChange={handleChange} />&nbsp;
                Lanza Rayos   <input type='checkbox' name="habilidades" value='Oído' onChange={handleChange} /><br></br>
                Artes Marciales   <input type='checkbox' name="habilidades" value='Artes Marciales' onChange={handleChange} />&nbsp;
                Inteligencia   <input type='checkbox' name="habilidades" value='Inteligencia' onChange={handleChange} />&nbsp;
                Acrobacia   <input type='checkbox' name="habilidades" value='Acrobacia' onChange={handleChange} />&nbsp;
                Armadura   <input type='checkbox' name="habilidades" value='Armadura' onChange={handleChange} />&nbsp;
                Tecnologia   <input type='checkbox' name="habilidades" value='Tecnologia' onChange={handleChange} />
                <br></br><br></br>
            </div>

            <button type='submit' className='btn btn-succes'>Enviar</button>
            <input type="reset" className='btn btn-primary' onClick="limpiar()" placeholder='limpiar' />

            {


              function limpiar() {
                window.location.href = window.location.href;
              }
            }
          </form>
        </div>
        <div class="col">
          <table className='table  table-hover'>
            <thead>
              <tr>
                <th>Nombre real</th>
                <th>Nombre de villano</th>
                <th>Edad</th>
                <th>Genero</th>
                <th>Origen</th>
                <th>Poder</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product) => (
                <tr key={product.id}>
                  <td>{product.nombrereal}</td>
                  <td>{product.nombredevillano}</td>
                  <td>{product.edad}</td>
                  <td>{product.genero}</td>
                  <td>{product.origen}</td>
                  <td>{product.poder}</td>
                  <td>

                    <Link to={`/edit/${product.id}`} className="btn btn-light"><i className="fa-solid fa-pencil"></i></Link>
                    <button onClick={() => { confirmDelete(product.id) }} className="btn btn-danger"><i className="fa-solid fa-trash"></i></button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>
    </div>
  )
}

export default Show
